import React from 'react';
import { Box, Typography, useTheme, useMediaQuery } from '@mui/material';
import Timeline from '../display/Timeline';

const PredictionResultTime = ({ prediction }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  if (!prediction) return null;

  return (
    <Box sx={{ width: '100%', display: 'flex', mt: isMobile ? 2 : 4, mb: isMobile ? 2 : 4, justifyContent: 'center', height: '100%'}}>
      <Box sx={{ 
        bgcolor: '#fff', 
        borderRadius: '14px',
        boxShadow: { xs: 4, md: 8 }, 
        p: { xs: 2, sm: 3 }, 
        width: '100%', 
        maxWidth: 1300, 
        display: 'flex', 
        flexDirection: 'column', 
        gap: isMobile ? 1 : 1.5 
      }}>
        {prediction.llm_explanation && (
          <Box sx={{ mt: isMobile ? 0.5 : 1, mb: isMobile ? 0.5 : 1, p: isMobile ? 1 : 1.5, bgcolor: '#f5f5f5', borderRadius: 2, width: '100%' }}>
            <Typography sx={{ 
              color: '#444', 
              fontSize: isMobile ? '0.9rem' : '1rem', 
              lineHeight: 1.7, 
              textAlign: 'center' 
            }}>
              {prediction.llm_explanation}
            </Typography>
          </Box>
        )}
        <Timeline prediction={prediction} />
      </Box>
    </Box>
  );
};

export default PredictionResultTime;
