import React from 'react';
import { Box, Typography } from '@mui/material';

const ExplanationChart = ({ explanations }) => {
  // Sortiere die Erklärungen nach absolutem Einfluss
  const sortedExplanations = [...explanations].sort((a, b) => 
    Math.abs(b.impact) - Math.abs(a.impact)
  );

  const getImpactColor = (impact) => {
    return impact > 0 ? '#4caf50' : '#f44336';
  };

  return (
    <Box>
      {sortedExplanations.map((explanation, index) => (
        <Box key={index} sx={{ mb: 2 }}>
          <Box sx={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            mb: 0.5
          }}>
            <Typography sx={{
              fontSize: '0.9rem',
              color: '#1a1a1a',
              fontWeight: 500
            }}>
              {explanation.feature}
            </Typography>
            <Typography sx={{
              fontSize: '0.9rem',
              fontWeight: 600,
              color: getImpactColor(explanation.impact)
            }}>
              {explanation.impact > 0 ? '+' : ''}{Math.round(explanation.impact * 100)}%
            </Typography>
          </Box>

          <Box sx={{ 
            position: 'relative',
            height: '8px',
            bgcolor: '#f5f5f5',
            borderRadius: '4px',
            overflow: 'hidden'
          }}>
            <Box sx={{
              position: 'absolute',
              top: 0,
              left: explanation.impact < 0 ? 'auto' : '50%',
              right: explanation.impact < 0 ? '50%' : 'auto',
              bottom: 0,
              width: `${Math.abs(explanation.impact * 100)}%`,
              bgcolor: getImpactColor(explanation.impact),
              borderRadius: '4px'
            }} />
          </Box>

          <Typography sx={{
            fontSize: '0.8rem',
            color: '#666',
            mt: 0.5
          }}>
            {explanation.description}
          </Typography>
        </Box>
      ))}
    </Box>
  );
};

export default ExplanationChart;
