import React from 'react';
import { Box, Typography } from '@mui/material';

const PredictionResult = ({ prediction }) => {
  if (!prediction) return null;

  const recommendations = Array.isArray(prediction.recommendations) 
    ? prediction.recommendations 
    : [prediction.recommendations];

  const getProbabilityClass = (confidence) => {
    if (confidence < 50) return 'probability-low-single';
    if (confidence < 75) return 'probability-medium-single';
    return 'probability-high-single';
  };

  const confidence = prediction.confidence * 100;
  const probabilityClass = getProbabilityClass(confidence);

  return (
    <Box
      className="form-group"
      sx={{
        bgcolor: 'white',
        borderRadius: 4,
        p: 4,
        boxShadow: '0 8px 25px rgba(0, 0, 0, 0.1)',
        mb: 4
      }}
    >
      <Typography
        variant="h6"
        sx={{
          color: '#0A1929',
          fontWeight: 600,
          mb: 3
        }}
      >
        Analyse-Ergebnisse
      </Typography>

      <Box className="prediction-section">
        <Box className="probability-section" sx={{ mb: 3 }}>
          <Box
            className="probability-header"
            sx={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              mb: 2
            }}
          >
            <Typography
              variant="h6"
              sx={{
                color: '#0A1929',
                fontSize: '1.1rem',
                m: 0
              }}
            >
              Wahrscheinlichkeit der Vorhersage
            </Typography>
            <Typography
              className="probability-value"
              sx={{
                fontWeight: 600,
                fontSize: '1.2rem'
              }}
            >
              {Math.round(confidence)}%
            </Typography>
          </Box>

          <Box
            className="probability-bar-container"
            sx={{
              width: '100%',
              height: '8px',
              bgcolor: '#f0f0f0',
              borderRadius: '4px',
              overflow: 'hidden'
            }}
          >
            <Box
              className={`probability-bar-single ${probabilityClass}`}
              sx={{
                height: '100%',
                width: `${confidence}%`,
                bgcolor: probabilityClass === 'probability-low-single' ? '#dc3545' :
                        probabilityClass === 'probability-medium-single' ? '#ffc107' : '#28a745',
                transition: 'width 0.3s ease'
              }}
            />
          </Box>
        </Box>

        <Box className="recommendations-section">
          <Typography
            variant="h6"
            sx={{
              color: '#0A1929',
              fontSize: '1.1rem',
              mb: 2
            }}
          >
            Empfohlene nächste Positionen
          </Typography>

          <Box
            component="ul"
            className="recommendation-list"
            sx={{
              listStyle: 'none',
              p: 0,
              m: 0
            }}
          >
            {recommendations && recommendations.length > 0 ? (
              recommendations.map((rec, index) => (
                <Box
                  component="li"
                  key={index}
                  className="recommendation-item"
                  sx={{
                    p: '12px 15px',
                    bgcolor: 'rgba(242, 242, 242, 0.6)',
                    borderRadius: '6px',
                    mb: 1.25,
                    fontSize: '0.95rem',
                    color: '#333',
                    '&:last-child': {
                      mb: 0
                    }
                  }}
                >
                  {rec}
                </Box>
              ))
            ) : (
              <Box
                component="li"
                className="recommendation-item"
                sx={{
                  p: '12px 15px',
                  bgcolor: 'rgba(242, 242, 242, 0.6)',
                  borderRadius: '6px',
                  fontSize: '0.95rem',
                  color: '#333'
                }}
              >
                Keine Empfehlungen verfügbar
              </Box>
            )}
          </Box>
        </Box>
      </Box>
    </Box>
  );
};

export default PredictionResult;
