import React, { useState } from 'react';
import { TextField, Button, Container, Typography, Box, Paper } from '@mui/material';
import ProfileDisplay from './ProfileDisplay';
import PredictionResult from './PredictionResult';

const LinkedInInput = () => {
  const [url, setUrl] = useState('');
  const [profile, setProfile] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // LinkedIn Profil abrufen
      const profileResponse = await fetch('/scrape-linkedin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url }),
      });

      const profileData = await profileResponse.json();

      if (profileData.error) {
        throw new Error(profileData.error);
      }

      setProfile(profileData);

      // Vorhersage abrufen
      const predictionResponse = await fetch('/predict', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          experiences: profileData.experience,
          modelType: 'tft'
        }),
      });

      const predictionData = await predictionResponse.json();

      if (predictionData.error) {
        throw new Error(predictionData.error);
      }

      setPrediction(predictionData);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Typography variant="h1" gutterBottom align="left" sx={{ mb: 4 }}>
        LinkedIn Analyse
      </Typography>

      <Typography variant="body1" sx={{ mb: 4 }}>
        Geben Sie einen LinkedIn-Profillink ein, um automatisch eine Karriereprognose basierend auf den verf√ºgbaren Berufserfahrungen zu erstellen.
      </Typography>

      <Paper elevation={0} sx={{ p: 4, mb: 4, borderRadius: 2 }}>
        <Typography variant="h2" gutterBottom sx={{ mb: 3 }}>
          LinkedIn Profil
        </Typography>

        <Box component="form" onSubmit={handleSubmit} sx={{ width: '100%' }}>
          <TextField
            fullWidth
            placeholder="https://www.linkedin.com/in/beispiel-profil"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            sx={{ mb: 2 }}
          />
          <Button
            variant="contained"
            type="submit"
            disabled={loading}
            fullWidth
            sx={{ 
              py: 1.5,
              textTransform: 'none',
              fontSize: '1rem'
            }}
          >
            {loading ? 'Wird analysiert...' : 'Profil analysieren'}
          </Button>
        </Box>

        {error && (
          <Typography color="error" sx={{ mt: 2 }}>
            {error}
          </Typography>
        )}
      </Paper>

      {profile && <ProfileDisplay profile={profile} />}
      {prediction && <PredictionResult prediction={prediction} loading={loading} />}
    </Container>
  );
};

export default LinkedInInput;
