import React from 'react';
import { Box, Typography, Paper } from '@mui/material';

const zeitraumRanges = [
  { label: "0-6 Monate", start: 0, end: 6, color: "#28a745" },
  { label: "7-12 Monate", start: 7, end: 12, color: "#ffc107" },
  { label: "13-24 Monate", start: 13, end: 24, color: "#dc3545" },
  { label: "über 24 Monate", start: 25, end: 36, color: "#b0b0b0" }
];

const PredictionResult = ({ prediction }) => {
  if (!prediction) return null;

  // TFT-spezifisch: Zeitraum und Confidence
  const { zeitraum, confidence } = prediction;
  const range = zeitraumRanges.find(r => r.label === zeitraum) || zeitraumRanges[3];

  return (
    <Box sx={{ mt: 4 }}>
      <Paper elevation={3} sx={{ p: 3, borderRadius: 2 }}>
        <Typography variant="h6" color="primary" gutterBottom>
          Vorhergesagter Wechselzeitraum
        </Typography>
        <Box sx={{ width: '100%', mt: 2, mb: 2 }}>
          {/* Zeitstrahl */}
          <Box sx={{ position: 'relative', height: 30, bgcolor: '#f0f0f0', borderRadius: 2 }}>
            {/* Markiere den Bereich */}
            <Box
              sx={{
                position: 'absolute',
                left: `${(range.start / 36) * 100}%`,
                width: `${((range.end - range.start + 1) / 36) * 100}%`,
                height: '100%',
                bgcolor: range.color,
                borderRadius: 2,
                opacity: 0.7
              }}
            />
            {/* Zeitstrahl-Beschriftung */}
            <Box sx={{ position: 'absolute', top: 32, left: 0, width: '100%', display: 'flex', justifyContent: 'space-between', fontSize: 14, color: '#666' }}>
              <span>0</span>
              <span>6</span>
              <span>12</span>
              <span>24</span>
              <span>36+ Monate</span>
            </Box>
          </Box>
        </Box>
        <Typography variant="h5" sx={{ color: range.color, fontWeight: 700, mt: 2 }}>
          {zeitraum}
        </Typography>
        <Typography sx={{ mt: 2, color: '#444' }}>
          {zeitraum === "0-6 Monate" && "Jetzt ist der ideale Zeitpunkt für eine Ansprache!"}
          {zeitraum === "7-12 Monate" && "In den nächsten Monaten könnte ein Wechsel interessant werden."}
          {zeitraum === "13-24 Monate" && "Mittelfristig beobachten, noch kein akuter Wechselbedarf."}
          {zeitraum === "über 24 Monate" && "Aktuell wenig Wechselbereitschaft, langfristig in Kontakt bleiben."}
        </Typography>
        {prediction.llm_explanation && (
          <Box sx={{ mt: 3, p: 2, bgcolor: '#f5f5f5', borderRadius: 2 }}>
            <Typography sx={{ color: '#444', fontSize: '1.1rem', lineHeight: 1.9 }}>
              {prediction.llm_explanation}
            </Typography>
          </Box>
        )}
      </Paper>
    </Box>
  );
};

export default PredictionResult;
