import React from 'react';
import { Box, Typography, List, ListItem, ListItemText, CircularProgress, Paper } from '@mui/material';

const PredictionResult = ({ prediction, loading }) => {
  if (loading) {
    return (
      <Box sx={{ 
        display: 'flex', 
        flexDirection: 'column',
        alignItems: 'center',
        gap: '20px',
        my: 4
      }}>
        <CircularProgress sx={{ color: 'primary.light' }} />
        <Typography variant="h6">
          Analysiere Karrieredaten...
        </Typography>
      </Box>
    );
  }

  if (!prediction) return null;

  const getConfidenceColor = (confidence) => {
    const value = confidence * 100;
    if (value < 33) return 'error.main';
    if (value < 66) return 'warning.main';
    return 'success.main';
  };

  return (
    <Paper elevation={0} sx={{ maxWidth: 800, mx: 'auto', mt: 5, p: 4 }}>
      <Typography variant="h2" align="center" gutterBottom>
        Vorhersageergebnis
      </Typography>

      <Box sx={{ my: 4 }}>
        <Box sx={{ 
          display: 'flex',
          alignItems: 'center',
          gap: 2,
          mb: 3
        }}>
          <Typography variant="h6" sx={{ 
            fontWeight: 600,
            fontSize: '2rem',
            minWidth: 80,
            color: 'primary.main'
          }}>
            {Math.round(prediction.confidence * 100)}%
          </Typography>
          <Box sx={{ 
            flexGrow: 1,
            height: 30,
            bgcolor: 'rgba(233, 236, 239, 0.5)',
            borderRadius: '15px',
            overflow: 'hidden',
            position: 'relative'
          }}>
            <Box sx={{
              width: `${prediction.confidence * 100}%`,
              height: '100%',
              bgcolor: getConfidenceColor(prediction.confidence),
              borderRadius: '15px',
              transition: 'width 1s ease-out'
            }} />
          </Box>
        </Box>
      </Box>

      {prediction.recommendations && prediction.recommendations.length > 0 && (
        <Box sx={{ mt: 4 }}>
          <Typography variant="h6" gutterBottom sx={{ 
            fontSize: '1.6rem',
            fontWeight: 600,
            color: 'primary.main',
            mb: 3
          }}>
            Empfehlungen
          </Typography>
          <List sx={{ 
            bgcolor: 'background.paper',
            borderRadius: '10px',
            boxShadow: '0 10px 30px rgba(0, 0, 0, 0.1)'
          }}>
            {prediction.recommendations.map((recommendation, index) => (
              <ListItem key={index} sx={{
                p: 2,
                mb: 2,
                bgcolor: 'rgba(255, 255, 255, 0.1)',
                borderLeft: '4px solid',
                borderColor: 'primary.main',
                borderRadius: '8px',
                transition: 'transform 0.2s ease, box-shadow 0.2s ease',
                '&:hover': {
                  transform: 'translateX(5px)',
                  boxShadow: '0 5px 15px rgba(0, 0, 0, 0.1)'
                }
              }}>
                <ListItemText 
                  primary={recommendation}
                  primaryTypographyProps={{
                    sx: { fontSize: '1.2rem' }
                  }}
                />
              </ListItem>
            ))}
          </List>
        </Box>
      )}
    </Paper>
  );
};

export default PredictionResult;
