import React from 'react';
import { Box, Typography } from '@mui/material';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import FlagIcon from '@mui/icons-material/Flag';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';

const Timeline = ({ prediction }) => {
  if (!prediction) return null;
  const confidenceValue = Array.isArray(prediction.confidence) ? prediction.confidence[0] : prediction.confidence;
  const tageBisWechsel = Math.round(confidenceValue);
  const heute = new Date();
  const wechseldatum = new Date(heute.getTime() + tageBisWechsel * 24 * 60 * 60 * 1000);

  return (
    <Box sx={{ width: '100%', maxWidth: 700, mx: 'auto', my: 4, p: 4, bgcolor: '#fff', borderRadius: '20px', boxShadow: '0 4px 24px 0 rgba(0,0,0,0.08)', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 3 }}>
      <Typography variant="h5" sx={{ color: '#001B41', fontWeight: 700, mb: 2, letterSpacing: 1 }}>Prognose: Voraussichtlicher Jobwechsel</Typography>
      <Box sx={{ display: 'flex', alignItems: 'center', gap: 4, width: '100%', justifyContent: 'center' }}>
        {/* Heute-Karte */}
        <Box sx={{ bgcolor: '#001B41', color: '#fff', borderRadius: '16px', p: 3, minWidth: 140, display: 'flex', flexDirection: 'column', alignItems: 'center', boxShadow: '0 2px 8px #001B4144' }}>
          <CalendarTodayIcon sx={{ fontSize: 36, mb: 1 }} />
          <Typography sx={{ fontWeight: 800, fontSize: 20, letterSpacing: 1 }}>Heute</Typography>
          <Typography sx={{ fontWeight: 500, fontSize: 18, mt: 1 }}>{heute.toLocaleDateString('de-DE')}</Typography>
        </Box>
        {/* Pfeil */}
        <ArrowForwardIcon sx={{ fontSize: 48, color: '#b0b0b0' }} />
        {/* Wechsel-Karte */}
        <Box sx={{ bgcolor: '#FF8000', color: '#fff', borderRadius: '16px', p: 3, minWidth: 180, display: 'flex', flexDirection: 'column', alignItems: 'center', boxShadow: '0 2px 8px #FF800044' }}>
          <FlagIcon sx={{ fontSize: 36, mb: 1 }} />
          <Typography sx={{ fontWeight: 800, fontSize: 20, letterSpacing: 1 }}>Wechsel</Typography>
          <Typography sx={{ fontWeight: 700, fontSize: 20, mt: 1 }}>{wechseldatum.toLocaleDateString('de-DE')}</Typography>
          <Typography sx={{ fontWeight: 500, fontSize: 16, mt: 1, bgcolor: '#fff', color: '#FF8000', borderRadius: 2, px: 2, py: 0.5, boxShadow: '0 1px 4px #FF800022' }}>{tageBisWechsel === 0 ? 'heute' : `in ${tageBisWechsel} Tag${tageBisWechsel === 1 ? '' : 'en'}`}</Typography>
        </Box>
      </Box>
    </Box>
  );
};

export default Timeline;
