import React from 'react';
import { Box, List, ListItem, ListItemIcon, ListItemText, Paper } from '@mui/material';
import { Link, useLocation } from 'react-router-dom';
import PersonIcon from '@mui/icons-material/Person';
import GroupIcon from '@mui/icons-material/Group';
import LinkedInIcon from '@mui/icons-material/LinkedIn';

const Sidebar = () => {
  const location = useLocation();

  const menuItems = [
    { path: '/', icon: <PersonIcon />, text: 'Karriere-Prognose' },
    { path: '/batch', icon: <GroupIcon />, text: 'Batch-Prognose' },
    { path: '/linkedin', icon: <LinkedInIcon />, text: 'LinkedIn-Prognose' }
  ];

  return (
    <Paper
      elevation={0}
      sx={{
        width: 250,
        minHeight: '100vh',
        borderRight: '1px solid #E0E0E0',
        borderRadius: 0,
        bgcolor: '#FFFFFF'
      }}
    >
      <Box sx={{ p: 2, mb: 2 }}>
        <img 
          src="/logo.png" 
          alt="Logo" 
          style={{ 
            width: '100%', 
            maxWidth: 150,
            display: 'block',
            margin: '0 auto'
          }} 
        />
      </Box>

      <List>
        {menuItems.map((item) => (
          <ListItem
            button
            component={Link}
            to={item.path}
            key={item.path}
            selected={location.pathname === item.path}
            sx={{
              mb: 1,
              mx: 1,
              borderRadius: 1,
              '&.Mui-selected': {
                backgroundColor: 'primary.main',
                color: 'white',
                '& .MuiListItemIcon-root': {
                  color: 'white',
                },
                '&:hover': {
                  backgroundColor: 'primary.dark',
                },
              },
            }}
          >
            <ListItemIcon sx={{ 
              minWidth: 40,
              color: location.pathname === item.path ? 'white' : 'primary.main'
            }}>
              {item.icon}
            </ListItemIcon>
            <ListItemText primary={item.text} />
          </ListItem>
        ))}
      </List>
    </Paper>
  );
};

export default Sidebar;
