import React from 'react';
import { Box, Typography } from '@mui/material';

const ProfileDisplay = ({ profile }) => {
  if (!profile) return null;

  return (
    <Box
      className="profile-content"
      sx={{
        bgcolor: 'var(--white)',
        borderRadius: '16px',
        p: '30px',
        boxShadow: 'var(--shadow)',
        mb: '30px',
        width: '100%'
      }}
    >
      <Box className="profile-header" sx={{ display: 'flex', alignItems: 'flex-start', gap: 3, mb: 4 }}>
        {profile.imageUrl && (
          <img
            src={profile.imageUrl}
            alt="Profilbild"
            className="profile-image"
            style={{
              width: '100px',
              height: '100px',
              borderRadius: '50%',
              objectFit: 'cover'
            }}
          />
        )}

        <Box className="profile-info">
          <Typography
            className="profile-name"
            sx={{
              color: 'var(--text-color)',
              fontWeight: 600,
              mb: 1,
              fontSize: '1.5rem'
            }}
          >
            {profile.name || 'Kein Name verfügbar'}
          </Typography>

          <Typography
            className="profile-title"
            sx={{
              color: '#666',
              fontSize: '1.1rem',
              mb: 1
            }}
          >
            {profile.currentTitle || 'Keine Position angegeben'}
          </Typography>

          <Typography
            className="profile-location"
            sx={{
              color: '#888',
              fontSize: '0.9rem'
            }}
          >
            {profile.location || 'Kein Standort angegeben'}
          </Typography>

          {profile.industry && (
            <Typography
              className="profile-industry"
              sx={{
                color: 'var(--text-color)',
                fontSize: '0.9rem',
                mt: 1
              }}
            >
              Branche: {profile.industry}
            </Typography>
          )}
        </Box>
      </Box>

      {profile.summary && (
        <Box className="profile-summary" sx={{ mb: 4 }}>
          <Typography
            variant="h6"
            sx={{
              color: 'var(--text-color)',
              fontWeight: 600,
              mb: 2,
              fontSize: '1.3rem',
              paddingBottom: '10px',
              borderBottom: '2px solid var(--border-color)'
            }}
          >
            Zusammenfassung
          </Typography>
          <Typography
            sx={{
              color: 'var(--text-color)',
              whiteSpace: 'pre-line',
              fontSize: '0.9rem',
              lineHeight: 1.6
            }}
          >
            {profile.summary}
          </Typography>
        </Box>
      )}

      {profile.experience && profile.experience.length > 0 && (
        <Box className="profile-experience">
          <Typography
            variant="h6"
            sx={{
              color: 'var(--text-color)',
              fontWeight: 600,
              mb: 2,
              fontSize: '1.3rem',
              paddingBottom: '10px',
              borderBottom: '2px solid var(--border-color)'
            }}
          >
            Berufserfahrung
          </Typography>

          {profile.experience.map((exp, index) => (
            <Box
              key={index}
              className="experience-entry"
              sx={{
                py: 2.5,
                borderBottom: index < profile.experience.length - 1 ? '1px solid var(--border-color)' : 'none'
              }}
            >
              <Box className="entry-header" sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                <Typography
                  variant="h6"
                  sx={{
                    color: 'var(--text-color)',
                    fontSize: '1.1rem',
                    fontWeight: 600,
                    m: 0
                  }}
                >
                  {exp.title || 'Keine Position angegeben'}
                </Typography>

                <Typography
                  className="company"
                  sx={{
                    color: 'var(--text-color)',
                    fontSize: '1rem',
                    m: 0
                  }}
                >
                  {exp.company || 'Kein Unternehmen angegeben'}
                </Typography>

                <Typography
                  className="duration"
                  sx={{
                    color: '#666',
                    fontSize: '0.9rem',
                    m: 0,
                    opacity: 0.8
                  }}
                >
                  {exp.duration || 'Kein Zeitraum angegeben'}
                </Typography>
              </Box>
            </Box>
          ))}
        </Box>
      )}
    </Box>
  );
};

export default ProfileDisplay;
