import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#0A2647', // Dunkles Marineblau wie im Bild
    },
    background: {
      default: '#FFFFFF',
      paper: '#FFFFFF',
    },
  },
  typography: {
    h1: {
      fontSize: '2.5rem',
      fontWeight: 600,
      color: '#0A2647',
    },
    h2: {
      fontSize: '2rem',
      fontWeight: 500,
      color: '#0A2647',
    },
    h6: {
      fontSize: '1.25rem',
      fontWeight: 500,
      color: '#0A2647',
    },
    body1: {
      color: '#666666',
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        contained: {
          backgroundColor: '#0A2647',
          color: 'white',
          '&:hover': {
            backgroundColor: '#083358',
          },
        },
      },
    },
    MuiTextField: {
      styleOverrides: {
        root: {
          '& .MuiOutlinedInput-root': {
            '& fieldset': {
              borderColor: '#E0E0E0',
            },
            '&:hover fieldset': {
              borderColor: '#0A2647',
            },
          },
        },
      },
    },
    MuiCard: {
      styleOverrides: {
        root: {
          boxShadow: '0px 4px 12px rgba(0, 0, 0, 0.05)',
          borderRadius: '8px',
        },
      },
    },
  },
});

export default theme;
