import React from 'react';
import { Box, Paper, Typography, CircularProgress } from '@mui/material';

const PredictionResult = ({ prediction, loading }) => {
  if (loading) {
    return (
      <Box className="loader-container-prediction" sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!prediction || !prediction.confidence) {
    return null;
  }

  // Wahrscheinlichkeit als Prozent
  const confidence = Array.isArray(prediction.confidence) 
    ? prediction.confidence[0] * 100 
    : prediction.confidence * 100;

  // Bestimme die Farbe basierend auf der Wahrscheinlichkeit
  const getBarColor = (value) => {
    if (value < 50) return '#4CAF50';  // Grün für niedrige Wahrscheinlichkeit
    if (value < 75) return '#FFA726';  // Orange für mittlere Wahrscheinlichkeit
    return '#F44336';  // Rot für hohe Wahrscheinlichkeit
  };

  return (
    <Paper 
      elevation={0}
      className="prediction-result" 
      sx={{ 
        mt: 4,
        p: 4,
        bgcolor: 'background.paper'
      }}
    >
      <Box className="prediction-container">
        <Typography variant="h6" gutterBottom>
          Prognose-Ergebnis
        </Typography>

        <Box 
          className="probability-wrapper-single"
          sx={{
            position: 'relative',
            my: 3
          }}
        >
          <Typography 
            className="probability-value-single"
            variant="h4"
            sx={{
              position: 'absolute',
              left: '50%',
              top: '50%',
              transform: 'translate(-50%, -50%)',
              zIndex: 1,
              fontWeight: 'bold'
            }}
          >
            {`${confidence.toFixed(0)}%`}
          </Typography>

          <Box 
            className="probability-bar-container-single"
            sx={{
              width: '100%',
              height: '40px',
              bgcolor: '#f5f5f5',
              borderRadius: '20px',
              overflow: 'hidden'
            }}
          >
            <Box
              className="probability-bar-single"
              sx={{
                width: `${confidence}%`,
                height: '100%',
                bgcolor: getBarColor(confidence),
                transition: 'width 0.5s ease-in-out'
              }}
            />
          </Box>
        </Box>

        <Box className="prediction-details">
          <Typography variant="h6" className="recommendation-title" gutterBottom>
            Empfehlungen:
          </Typography>

          <Box 
            component="ul" 
            className="recommendation-list"
            sx={{
              listStyle: 'none',
              p: 0,
              m: 0
            }}
          >
            {prediction.recommendations ? (
              (Array.isArray(prediction.recommendations) 
                ? prediction.recommendations 
                : [prediction.recommendations]
              ).map((recommendation, index) => (
                <Box
                  component="li"
                  key={index}
                  className="recommendation-item"
                  sx={{
                    mb: 1,
                    p: 2,
                    bgcolor: 'rgba(0, 0, 0, 0.04)',
                    borderRadius: '8px'
                  }}
                >
                  {recommendation}
                </Box>
              ))
            ) : (
              <Box
                component="li"
                className="recommendation-item"
                sx={{
                  mb: 1,
                  p: 2,
                  bgcolor: 'rgba(0, 0, 0, 0.04)',
                  borderRadius: '8px'
                }}
              >
                Keine Empfehlungen verfügbar
              </Box>
            )}
          </Box>
        </Box>
      </Box>
    </Paper>
  );
};

export default PredictionResult;
