import React, { useState } from 'react';
import { Box, Paper } from '@mui/material';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = () => {
  const location = useLocation();
  const [hovered, setHovered] = useState(false);

  const expanded = hovered;

  const menuItems = [
    { 
      path: '/', 
      icon: '/static/images/carrer_nav.jpeg',
      text: 'Manuelle-Prognose',
      alt: 'Karriere Navigation'
    },
    { 
      path: '/linkedin', 
      icon: '/static/images/linkedin_nav.jpg',
      text: 'LinkedIn-Prognose',
      alt: 'LinkedIn Navigation'
    },
    { 
      path: '/batch', 
      icon: '/static/images/candi_batch_nav.jpg',
      text: 'Batch-Upload',
      alt: 'Batch Navigation'
    },
    { 
      path: '/candidates', 
      icon: '/static/images/candidates.png',
      text: 'Kandidaten',
      alt: 'Kandidaten Navigation'
    }
  ];

  const bottomButtons = [
    { icon: '/static/images/settings.svg', alt: 'Einstellungen' },
    { icon: '/static/images/info.svg', alt: 'Info' },
    { icon: '/static/images/plus.svg', alt: 'Hinzufügen' },
    { icon: '/static/images/arrow.svg', alt: 'Weiter' },
  ];

  return (
    <Paper
      elevation={3}
      sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: expanded ? '240px' : '100px',
        height: '100vh',
        bgcolor: '#fff',
        borderRadius: 0,
        boxShadow: '0 8px 32px rgba(0,0,0,0.08)',
        transition: 'width 0.25s cubic-bezier(.4,0,.2,1)',
        zIndex: 1000,
        overflow: 'hidden',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        border: 'none',
      }}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column', pt: expanded ? 3 : 2 }}>
        {/* Navigation */}
        <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column', gap: 0.5 }}>
          {menuItems.map((item) => (
            <Box
              key={item.path}
              component={Link}
              to={item.path}
              sx={{
                display: 'flex',
                alignItems: 'center',
                gap: expanded ? 2 : 0,
                px: expanded ? 2 : 1.2,
                py: expanded ? 1.5 : 1.2,
                borderRadius: 0,
                textDecoration: 'none',
                transition: 'background 0.2s, color 0.2s',
                bgcolor: location.pathname === item.path ? 'rgba(255,128,0,0.08)' : 'transparent',
                '&:hover': {
                  bgcolor: 'rgba(255,128,0,0.13)',
                },
              }}
            >
              <img
                src={item.icon}
                alt={item.alt}
                style={{
                  width: 28,
                  height: 28,
                  borderRadius: 0,
                  objectFit: 'cover',
                  transition: 'all 0.2s',
                }}
              />
              {expanded && (
                <Box
                  sx={{
                    color: location.pathname === item.path ? '#FF8000' : '#222',
                    fontWeight: location.pathname === item.path ? 700 : 500,
                    fontSize: '1rem',
                    whiteSpace: 'nowrap',
                  }}
                >
                  {item.text}
                </Box>
              )}
            </Box>
          ))}
        </Box>
      </Box>
    </Paper>
  );
};

export default Sidebar;
