import React, { useState } from 'react';
import { 
  TextField, 
  Button, 
  Grid, 
  Paper, 
  Typography,
  IconButton,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

const ExperienceForm = ({ onAddExperience, experiences }) => {
  const [formData, setFormData] = useState({
    position: '',
    company: '',
    startDate: '',
    endDate: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddExperience(formData);
    setFormData({
      position: '',
      company: '',
      startDate: '',
      endDate: ''
    });
  };

  return (
    <Paper sx={{ p: 3, mb: 3 }}>
      <Typography variant="h6" gutterBottom>
        Berufserfahrung hinzufügen
      </Typography>

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Position"
              name="position"
              value={formData.position}
              onChange={handleChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Unternehmen"
              name="company"
              value={formData.company}
              onChange={handleChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Startdatum (YYYY-MM)"
              name="startDate"
              value={formData.startDate}
              onChange={handleChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Enddatum (YYYY-MM)"
              name="endDate"
              value={formData.endDate}
              onChange={handleChange}
              placeholder="Leer lassen für aktuelle Position"
            />
          </Grid>
          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary">
              Hinzufügen
            </Button>
          </Grid>
        </Grid>
      </form>

      {experiences.length > 0 && (
        <List sx={{ mt: 2 }}>
          {experiences.map((exp, index) => (
            <ListItem key={index}>
              <ListItemText
                primary={exp.position}
                secondary={`${exp.company} - ${exp.startDate} bis ${exp.endDate || 'heute'}`}
              />
              <ListItemSecondaryAction>
                <IconButton edge="end" aria-label="delete">
                  <DeleteIcon />
                </IconButton>
              </ListItemSecondaryAction>
            </ListItem>
          ))}
        </List>
      )}
    </Paper>
  );
};

export default ExperienceForm;
